import { OpenQuestionStatus } from "@koh/common";
import { useProfile } from "./useProfile";
import { useQuestions } from "./useQuestions";

/**
 * SWR wrapper for the question of the currently logged-in student
 */
export function useStudentQuestion(qid) {
  const profile = useProfile();
  const {
    questions,
    questionsError,
    mutateQuestion
  } = useQuestions(qid);
  const studentQuestion = profile && questions && questions.find(q => q.creator.id === profile.id);
  const studentQuestionIndex = studentQuestion && questions.filter(question => question.status !== OpenQuestionStatus.CantFind && question.status !== OpenQuestionStatus.TADeleted && question.status !== OpenQuestionStatus.Helping).indexOf(studentQuestion);
  return {
    studentQuestion,
    studentQuestionIndex,
    studentQuestionError: questionsError,
    mutateStudentQuestion: mutateQuestion
  };
}
