export function getWaitTime(question) {
  const now = new Date();
  const difference = now.getTime() - question.createdAt.getTime();
  return `${Math.round(difference / 60000)} min`;
}
export function formatQueueTime(queue) {
  return formatDateTime(queue.startTime) + " - " + formatDateTime(queue.endTime);
}

function formatDateTime(date) {
  let hours = date.getHours();
  let minutes = date.getMinutes();
  const ampm = hours >= 12 ? "PM" : "AM";
  hours = hours % 12;
  hours = hours ? hours : 12; // the hour '0' should be '12'

  minutes = minutes < 10 ? "0" + minutes : minutes;
  return hours + ":" + minutes + " " + ampm;
}
